{"changed":true,"filter":false,"title":"ActiveDirectory.rb","tooltip":"/ActiveDirectory.rb","value":"require 'rubygems'\nrequire 'net/ldap'\n\nldap = Net::LDAP.new\nldap.host = \"adldap.cos.agilent.com\"\nldap.port = 389\nldap.auth \"semuruga\",\"passwd\" #\"flexcls\",\"passwd\" \"anisamym\", \"Setools@02\"\nif ldap.bind\n  puts \"authentication succeeded\"\nelse\n  puts \"authentication failed\"\nend\n\n\n##############################################################################3\n\n\n require 'net/ldap' # gem install ruby-net-ldap\n\n    class ActiveDirectoryUser\n      ### BEGIN CONFIGURATION ###\n      SERVER = \"adldap.cos.agilent.com\"   # Active Directory server name or IP\n      PORT = 389                    # Active Directory server port (default 389) 636\n      BASE = \"DC=agilent,DC=com\"    # Base to search from  DC=ldap,DC=it,DC=agilent,DC=com\n      DOMAIN = \"non.agilent.com\"        # For simplified user@domain format login\n      ### END CONFIGURATION ###\n\n      def self.authenticate(login, pass)\n        return false if login.empty? or pass.empty?\n        #puts login\n        #puts pass\n        \n        \n    $relative_to = 5000; \n        conn = Net::LDAP.new :host => SERVER,\n                             :port => PORT,\n                             :base => BASE,\n                             \n                             :auth => { :username => \"#{login}@#{DOMAIN}\", \n                                        :password => pass,\n                                        :method => :simple }\nputs \"#{login}@#{DOMAIN}\"\nputs pass\n        if conn.bind\n          puts \"true\"\n        else\n          puts \"false\"\n        end\n      # If we don't rescue this, Net::LDAP is decidedly ungraceful about failing\n      # to connect to the server. We'd prefer to say authentication failed.\n      rescue Net::LDAP::LdapError => e\n        return false\n      end\n    end\n    \n    ActiveDirectoryUser.authenticate(\"murali_arimuthu\",\"OMMuruga@123\")  #\"flexcls\",\"passwd\"\n    \n    \n  \n  \n  ####################################################################\n  \n  \n\n\n\n","undoManager":{"mark":-60,"position":100,"stack":[[{"start":{"row":14,"column":51},"end":{"row":14,"column":52},"action":"insert","lines":["#"],"id":59}],[{"start":{"row":14,"column":52},"end":{"row":14,"column":53},"action":"insert","lines":["#"],"id":60}],[{"start":{"row":14,"column":53},"end":{"row":14,"column":54},"action":"insert","lines":["#"],"id":61}],[{"start":{"row":14,"column":54},"end":{"row":14,"column":55},"action":"insert","lines":["#"],"id":62}],[{"start":{"row":14,"column":55},"end":{"row":14,"column":56},"action":"insert","lines":["#"],"id":63}],[{"start":{"row":14,"column":56},"end":{"row":14,"column":57},"action":"insert","lines":["#"],"id":64},{"start":{"row":14,"column":57},"end":{"row":14,"column":58},"action":"insert","lines":["#"]}],[{"start":{"row":14,"column":58},"end":{"row":14,"column":59},"action":"insert","lines":["#"],"id":65}],[{"start":{"row":14,"column":59},"end":{"row":14,"column":60},"action":"insert","lines":["#"],"id":66}],[{"start":{"row":14,"column":60},"end":{"row":14,"column":61},"action":"insert","lines":["#"],"id":67}],[{"start":{"row":14,"column":61},"end":{"row":14,"column":62},"action":"insert","lines":["#"],"id":68}],[{"start":{"row":14,"column":62},"end":{"row":14,"column":63},"action":"insert","lines":["#"],"id":69}],[{"start":{"row":14,"column":63},"end":{"row":14,"column":64},"action":"insert","lines":["#"],"id":70},{"start":{"row":14,"column":64},"end":{"row":14,"column":65},"action":"insert","lines":["#"]}],[{"start":{"row":14,"column":65},"end":{"row":14,"column":66},"action":"insert","lines":["#"],"id":71}],[{"start":{"row":14,"column":66},"end":{"row":14,"column":67},"action":"insert","lines":["#"],"id":72}],[{"start":{"row":14,"column":67},"end":{"row":14,"column":68},"action":"insert","lines":["#"],"id":73}],[{"start":{"row":14,"column":68},"end":{"row":14,"column":69